import React, { Component } from 'react';
import {BootstrapTable,
    TableHeaderColumn} from 'react-bootstrap-table';
import InsertButton from 'react-bootstrap-table/lib/toolbar/InsertButton';
import DeleteButton from 'react-bootstrap-table/lib/toolbar/DeleteButton';
import '../../css/Table.css';
import 'react-bootstrap-table/css/react-bootstrap-table.css'
import axios from "axios";
import {onAfterDeleteRow, sendEditCell, addTeam, makeDepressiveTeam, path_server} from "../../apiNet"
import {withRouter} from "react-router-dom";
import {path_server2} from "../../apiNet";

import 'react-bootstrap-table/dist/react-bootstrap-table.min.css';
import qs from 'qs';
import {Button, Modal} from "react-bootstrap";
import NameForm from "../Forms/CreateForms";
import 'bootstrap/dist/css/bootstrap.min.css';;
function formatterTeam(cell, row) {
    if (cell===null)
        return <span>-</span>
    return <div>
        <span>Id: {cell.id}</span>
        <br/>
        <span>Name: {cell.name}</span>
    </div>
}
class TableTeams extends React.Component{
    constructor(props) {
        super(props);
        debugger;
        console.log("!!!+!+!+!+!+!+!+!+!+!+!+!+!+!+!++!+!!+!+");
        this.getAllObject=this.getAllObject.bind(this);
        this.state = { data:[],
                       selectedRow:new Set(),
        };
    }
    onRowSelect(row, isSelected, e) {
        debugger;
        if(isSelected) {
            this.setState(({selectedRow}) => ({
                selectedRow: new Set(selectedRow).add(row)
            }));
        }
        else{
            if(this.state.selectedRow.size<=1){
                this.setState(({selectedRow}) => ({
                    selectedRow: new Set()
                }));
            }else {
                new Set(this.state.selectedRow).delete(row);
                this.setState(({selectedRow}) => ({
                    selectedRow: new Set(this.state.selectedRow)
                }));
            }
        }

    }
    createCustomDeleteButton = (onClick) => {
        return (
            <div className="margin">
                <label >Name team</label>
                <input  type="text" value={this.state.nameTeamForDepressive} onChange={this.handleChangeTeamNameForDepressive} />
                <DeleteButton
                    size=""
                    btnText='Make depressive team'
                    className="btn-padding-left-top"
                    onClick={ e => {   this.setState(({selectedRow}) => ({selectedRow: new Set()}));
                        makeDepressiveTeam(this.state.nameTeamForDepressive
                        ) }}/>
                <br/>
                <br/>
            </div>
        )};

    componentDidMount() {
        debugger;
        this.getAllObject();
        this.timer = setInterval(() => this.getAllObject(), 100000);
    }
    render() {
        const options = {
            hideSizePerPage: true,
            sizePerPage: 5,
            insertBtn: this.createCustomInsertButton
        };
        const selectRowProp = {
            mode: 'checkbox',
            columnWidth: '2%',
            onSelect: this.onRowSelect,
        };
        const cellEditProp = {
            mode: 'dbclick', // 'dbclick' for trigger by double-click
            afterSaveCell: sendEditCell,
            blurToSave: true
        }

        return ( <BootstrapTable
            remote
            data={this.state}
            dataField='id'
            tdStyle={ { whiteSpace: 'normal' } }
            insertRow
            cellEdit={cellEditProp}
            selectRow={ selectRowProp }
            deleteRow={ true }
            options={ options }
            striped
            hover
            containerClass='table'
        >
            <TableHeaderColumn isKey
                               dataSort
                               dataField='id'
                               headerAlign="left"
                               width="1%"
            >
                ID
            </TableHeaderColumn>

            <TableHeaderColumn dataField='name'
                               dataSort
                               dataAlign='center'
                               headerAlign="center"
                               width="4%"
                               filter={ { type: 'TextFilter' } }
            >
                Name
            </TableHeaderColumn>

                <TableHeaderColumn dataField='team'
                                   dataSort
                                   dataAlign='center'
                                   headerAlign="center"
                                   width="2%"
                                   dataFormat={formatterTeam}
                >
                    Team
                </TableHeaderColumn>
        </BootstrapTable>
        )
    }

    getAllObject() {
        axios.get(path_server2+"all")
            .then(res => {
                const msg = res.data;
                if(msg.code ===1){
                    let k=msg.data;
                    this.setState({data: k});
                }
            }).catch(err => {
                console.log(err);
        });
    }


}

export default withRouter(TableTeams);
